('530d29ca-17a1-4db9-a569-09a17e30b0af', N'Giao hàng nhanh', 0, N'Dịch vụ Giao hàng nhanh cung cấp dịch vụ giao hàng cho các đơn hàng nhỏ và cần được giao đi trong thời gian ngắn.', 'https://detoivn.b-cdn.net/services/dondep/category.png', '1b1a6ebd-2838-4b3d-a1f1-1818305df2d6'),
('9f6bf976-ec5d-4e31-bfeb-7aea7637f20e', N'Chăm sóc người già, trẻ em', 0, N'Dịch vụ Chăm sóc người già, trẻ em cung cấp các dịch vụ chăm sóc và giám sát cho người già và trẻ em trong gia đình của bạn.', 'https://detoivn.b-cdn.net/services/dondep/category.png', '6f57d993-eb26-4b35-8c7d-7871a7fd624f'),
('b1c2cd51-bbe5-4b95-bcf4-3f04b57357d0', N'Mua sắm tại siêu thị', 0, N'Dịch vụ Mua sắm tại siêu thị cung cấp dịch vụ mua sắm tại các siêu thị hoặc cửa hàng để bạn không phải mất thời gian và công sức.', 'https://detoivn.b-cdn.net/services/dondep/category.png', '6f57d993-eb26-4b35-8c7d-7871a7fd624f'),
('8a4ad0e1-16fd-457e-a6f0-16accaf8e764', N'Vận chuyển đồ đạc', 0, N'Dịch vụ Vận chuyển đồ đạc cung cấp dịch vụ chuyển phát nhanh cho các đồ đạc như hành lý cá nhân, đồ dùng gia đình, và các vật phẩm cá nhân khác.', 'https://detoivn.b-cdn.net/services/dondep/category.png', '1b1a6ebd-2838-4b3d-a1f1-1818305df2d6'),
('f146cba9-72a1-47be-a36d-9bf6407f59d9', N'Đi chợ', 0, N'Dịch vụ Đi chợ cung cấp dịch vụ mua sắm tại các cửa hàng, siêu thị hoặc chợ truyền thống để thuận tiện cho bạn.', 'https://detoivn.b-cdn.net/services/dondep/category.png', '6f57d993-eb26-4b35-8c7d-7871a7fd624f'),
('531f4e94-1880-4d31-b001-6f59ff0322f0', N'Nấu ăn', 0, N'Dịch vụ Nấu ăn cung cấp dịch vụ chuẩn bị và nấu các bữa ăn ngon miệng và dinh dưỡng cho gia đình hoặc sự kiện của bạn.', 'https://detoivn.b-cdn.net/services/dondep/category.png', '6f57d993-eb26-4b35-8c7d-7871a7fd624f'),
('dc3262c0-6cf9-4058-be3c-98e4bb401ada', N'Giặt ủi', 0, N'Dịch vụ Giặt ủi cung cấp dịch vụ giặt và ủi quần áo và vật dụng gia đình để bạn có thời gian và năng lượng cho những việc khác.', 'https://detoivn.b-cdn.net/services/dondep/category.png', '6f57d993-eb26-4b35-8c7d-7871a7fd624f'),
('80998115-c96e-43f8-80b9-2c37272be33f', N'Sửa chữa máy lạnh', 0, N'Dịch vụ Sửa chữa máy lạnh cung cấp các dịch vụ sửa chữa, bảo dưỡng và lắp đặt cho máy lạnh tại nhà hoặc cơ sở của bạn.', 'https://detoivn.b-cdn.net/services/dondep/category.png', '8a21b21e-dc31-49c8-8b5b-84b69204dc3a'),
('509827e5-0852-4ea2-9fa4-7551bbbf88d9', N'Sửa chữa ống nước', 0, N'Dịch vụ Sửa chữa ống nước cung cấp các dịch vụ sửa chữa và bảo dưỡng cho hệ thống ống nước của bạn, bao gồm việc sửa chữa các rò rỉ và thay thế ống hỏng.', 'https://detoivn.b-cdn.net/services/dondep/category.png', '8a21b21e-dc31-49c8-8b5b-84b69204dc3a'),
